class Blog{constructor(e,t){this.id=e,this.key=t,this._url="https://www.googleapis.com/blogger/v3/blogs/"+this.id+"/"}async requestPost(e){return await this._request(this._url+"posts/"+e+"?key="+this.key+"&fields=title,content,updated,labels,kind")}async requestPage(e){return await this._request(this._url+"pages/"+e+"?key="+this.key+"&fields=title,content,updated,kind")}async requestFeed(e){return await this._request(this._url+"posts?key="+this.key+"&labels="+e+"&fields=nextPageToken,items(title,content,id,labels)")}async requestNextFeed(e,t){return await this._request(this._url+"posts?key="+this.key+"&labels="+e+"&pageToken="+t+"&fields=nextPageToken,items(title,content,id,labels)")}async _request(e){return await new Promise(t=>{const s=new XMLHttpRequest;s.open("GET",e),s.onload=(e=>{let i=JSON.parse(s.responseText);t(i)}),s.onerror=function(){t(void 0)},s.send()})}}